def computingfrequencies(string, k, kmerMaster, frequencies): ##faster kmer finder
    frequenciesString=""
    kmerList = kmerMaster  
    freq=frequencies
    for i in range(len(string)-k+1):
        kmer = list(string[i:(i+k)])
        index = kmerList.index(kmer)
        freq[index]+=1
    return freq


